const int N = 2e6 + 7;

struct Combination {
  int fac[N], inv[N];
  Combination() {init(N-1); }
  void init(const int &n) {
    fac[0] = inv[0] = fac[1] = inv[1] = 1;
    for (int i = 2; i <= n; ++i) {
      fac[i] = 1ll*fac[i-1]*i%MOD;
      inv[i] = 1ll*(MOD-MOD/i)*inv[MOD%i]%MOD;
    }
    for (int i = 2; i <= n; ++i) {
      inv[i] = 1ll*inv[i]*inv[i-1]%MOD;
    }
  }
  int A(const int &p, const int &q) {
    return p >= q ? 1ll*fac[p]*inv[p-q]%MOD : 0;
  }
  int C(const int &p, const int &q) {
    return p >= q ? 1ll*fac[p]*inv[q]%MOD*inv[p-q]%MOD : 0;
  }
};

int n, k;
Combination comb;
constexpr int Gap = 100'000;
int a[N];

Mint fac(int p) {
  if (p < N) return comb.fac[p];
  if (p >= MOD) return 0;
  Mint val = fff[p / Gap];
  for (int i = 1, base = p / Gap * Gap; i <= p % Gap; ++i) {
    val *= base + i;
  }
  return val;
}

Mint C(int p, int q) {
  if (p < N) return comb.C(p, q);
  if (p >= MOD) return 0;
  return fac(p) / (fac(q) * fac(p - q));
}

inline Mint solve() {
  cin >> n >> k;
  ll sum = 0;
  for (int i = 1; i <= n; ++i) {
    cin >> a[i];
    sum += a[i];
  }
  sum -= a[1];
  a[1] -= k;
  if (a[1] < sum) return 0;
  a[1] -= sum;
  Mint ans = C(a[1] + k - 1, k - 1);
  for (int i = 2; i <= n; ++i) {
    // a[i] <= a[1] - k
    // a[i] + k - 1 < MOD
    ans *= C(a[i] + k - 1, k - 1);
  }
  return ans;
}

signed main() {
#ifdef ONLINE_JUDGE
  ios::sync_with_stdio(false); cin.tie(nullptr); cout.tie(nullptr);
#endif
  int T = 1;
  // cin >> T; // scanf("%d", &T);
  for (int t = 1; t <= T; ++t) {
    cout << solve() << '\n';
  }
  return 0;
}